function fullscreenParallax(){var e=$(window);$(".parallax").each(function(){var t=$(this);t.css({"background-image":"url("+t.data("parallax-image")+")","background-attachment":"fixed","background-size":"cover"});e.scroll(function(){var n=(t.offset().top-e.scrollTop())*t.data("background-speed");if(t.data("parallax-stick")==="bottom"){n+=e.height()}t.css({"background-position":"50% "+n+"px"})})});$(window).resize(function(){$(".parallax").css({height:e.height()});$(window).scroll()});$(window).resize()}